import React, { useState } from "react";
import axios from "axios";

function App() {
  const [answers, setAnswers] = useState(Array(10).fill(3));
  const [result, setResult] = useState("");

  const handleChange = (index, value) => {
    const newAnswers = [...answers];
    newAnswers[index] = parseInt(value);
    setAnswers(newAnswers);
  };

  const handleSubmit = async () => {
    try {
      const res = await axios.post("http://localhost:8000/predict-style", {
        answers: answers
      });
      setResult(res.data.learning_style);
    } catch (err) {
      console.error(err);
      setResult("Error predicting style");
    }
  };

  return (
    <div className="container">
      <h2>EduAI Learning Style Predictor</h2>
      {answers.map((a, i) => (
        <div key={i} className="question">
          <label>Question {i + 1}:</label>
          <input
            type="number"
            min="1"
            max="5"
            value={a}
            onChange={(e) => handleChange(i, e.target.value)}
          />
        </div>
      ))}
      <button onClick={handleSubmit}>Predict Learning Style</button>
      {result && <h3 className="result">Predicted Style: {result}</h3>}
    </div>
  );
}

export default App;
