import React, { useState } from "react";
import axios from "axios";

const questions = [
  "I prefer learning by seeing diagrams and charts.",
  "I remember things better when I hear explanations.",
  "I like taking notes and reading texts.",
  "I prefer learning by doing tasks.",
  "Visual aids help me understand concepts.",
  "Listening to lectures helps me retain information.",
  "Summarizing text helps me remember.",
  "Hands-on practice improves my learning.",
  "I learn better with charts and visuals.",
  "Interactive exercises enhance my understanding."
];

function App() {
  const [answers, setAnswers] = useState(Array(questions.length).fill(""));
  const [result, setResult] = useState("");

  const handleChange = (index, value) => {
    const newAnswers = [...answers];
    newAnswers[index] = value;
    setAnswers(newAnswers);
  };

  const handleSubmit = async () => {
    try {
      const res = await axios.post("http://localhost:8000/predict-style", {
        answers: answers
      });
      setResult(res.data.learning_style);
    } catch (err) {
      console.error(err);
      setResult("Error predicting learning style");
    }
  };

  return (
    <div className="container">
      <h2>EduAI Learning Style Predictor</h2>
      {questions.map((q, i) => (
        <div key={i} className="question">
          <p>{i + 1}. {q}</p>
          <textarea
            value={answers[i]}
            onChange={(e) => handleChange(i, e.target.value)}
            placeholder="Your answer..."
            rows={2}
            style={{ width: "100%", marginBottom: "10px" }}
          />
        </div>
      ))}
      <button onClick={handleSubmit}>Predict Learning Style</button>
      {result && <div className="result"><h3>Predicted Style:</h3><p>{result}</p></div>}
    </div>
  );
}

export default App;
